{
  "name": "FloraGuard AI: Vegetable Plant Doctor",
  "description": "An intelligent plant disease detection and agronomist chatbot that helps you identify vegetable leaf diseases and provides expert treatment and prevention advice.",
  "requestFramePermissions": [
    "camera"
  ]
}

import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

const rootElement = document.getElementById('root');
if (!rootElement) {
  throw new Error("Could not find root element to mount to");
}

const root = ReactDOM.createRoot(rootElement);
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

export interface Message {
  role: 'user' | 'model';
  text: string;
  image?: string;
  isAnalysis?: boolean;
}

export interface DiseaseAnalysis {
  plantName: string;
  diseaseName: string;
  severity: string;
  symptoms: string[];
  cures: {
    organic: string[];
    chemical: string[];
  };
  prevention: string[];
}

export interface ChatState {
  messages: Message[];
  isAnalyzing: boolean;
  error: string | null;
}

import { GoogleGenAI, GenerateContentResponse, Type } from "@google/genai";
import { DiseaseAnalysis } from "../types";

const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

const SYSTEM_INSTRUCTION = `You are an expert vegetable plant pathologist and agronomist. 
Your goal is to identify diseases in vegetable plant leaves accurately and provide clear, professional advice on treatment and prevention.
When analyzing an image, focus on the specific visual symptoms. 
Provide organic and chemical solutions. 
Always prioritize safety and environmental responsibility.`;

export const analyzePlantImage = async (base64Image: string): Promise<DiseaseAnalysis> => {
  const model = 'gemini-3-flash-preview';

  const prompt = `Analyze this vegetable plant leaf image. Identify:
1. The common name of the plant.
2. The specific disease name (if any).
3. Visual symptoms observed.
4. Organic cures.
5. Recommended chemical/pesticide treatments.
6. Preventive measures to stop the spread.

Return the data strictly in JSON format matching this schema:
{
  "plantName": string,
  "diseaseName": string,
  "severity": "Low" | "Medium" | "High",
  "symptoms": string[],
  "cures": {
    "organic": string[],
    "chemical": string[]
  },
  "prevention": string[]
}`;

  const response = await ai.models.generateContent({
    model,
    contents: {
      parts: [
        { inlineData: { mimeType: 'image/jpeg', data: base64Image } },
        { text: prompt }
      ]
    },
    config: {
      systemInstruction: SYSTEM_INSTRUCTION,
      responseMimeType: "application/json"
    }
  });

  const text = response.text;
  if (!text) throw new Error("No response from AI");

  try {
    return JSON.parse(text) as DiseaseAnalysis;
  } catch (err) {
    console.error("Failed to parse AI response:", text);
    throw new Error("Invalid response format from AI");
  }
};

export const chatWithBotanist = async (history: { role: 'user' | 'model', text: string }[], newMessage: string) => {
  const chat = ai.chats.create({
    model: 'gemini-3-flash-preview',
    config: {
      systemInstruction: SYSTEM_INSTRUCTION,
    }
  });

  // Re-inject history
  // Note: Gemini Chat API usually expects history to be set at creation or via sequence of sendMessages.
  // For simplicity and to avoid complex history management in a stateless way, 
  // we can use the chat object or just generateContent with a combined prompt.
  // Using the built-in Chat object:

  // We'll simulate history for the first message if needed, but since we are in a session:
  const response = await chat.sendMessage({ message: newMessage });
  return response.text;
};

import React, { useRef } from 'react';

interface ImageUploadProps {
  onImageSelected: (base64: string) => void;
  disabled?: boolean;
}

const ImageUpload: React.FC<ImageUploadProps> = ({ onImageSelected, disabled }) => {
  const fileInputRef = useRef<HTMLInputElement>(null);

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = () => {
        const base64String = (reader.result as string).split(',')[1];
        onImageSelected(base64String);
      };
      reader.readAsDataURL(file);
    }
  };

  return (
    <div className="w-full max-w-xl mx-auto p-6 bg-white rounded-2xl border-2 border-dashed border-emerald-200 hover:border-emerald-400 transition-colors cursor-pointer group relative overflow-hidden shadow-sm">
      <input
        type="file"
        accept="image/*"
        onChange={handleFileChange}
        className="absolute inset-0 opacity-0 cursor-pointer z-10"
        disabled={disabled}
      />
      <div className="flex flex-col items-center justify-center space-y-4 py-8">
        <div className="p-4 bg-emerald-50 rounded-full group-hover:bg-emerald-100 transition-colors">
          <svg className="w-10 h-10 text-emerald-600" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z" />
          </svg>
        </div>
        <div className="text-center">
          <p className="text-lg font-medium text-slate-700">Upload a photo of the leaf</p>
          <p className="text-sm text-slate-500">Take a clear picture of the infected area</p>
        </div>
      </div>
    </div>
  );
};

export default ImageUpload;

