import React, { useState, useRef, useEffect } from 'react';
import { Message } from '../types';

interface ChatWindowProps {
  messages: Message[];
  onSendMessage: (text: string) => void;
  isLoading: boolean;
}

const ChatWindow: React.FC<ChatWindowProps> = ({ messages, onSendMessage, isLoading }) => {
  const [input, setInput] = useState('');
  const scrollRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    if (scrollRef.current) {
      scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
    }
  }, [messages, isLoading]);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (input.trim() && !isLoading) {
      onSendMessage(input.trim());
      setInput('');
    }
  };

  return (
    <div className="flex flex-col h-[600px] bg-white rounded-2xl shadow-lg border border-slate-100 overflow-hidden">
      <div className="p-4 bg-emerald-600 text-white flex items-center justify-between">
        <div className="flex items-center space-x-3">
          <div className="w-10 h-10 rounded-full bg-emerald-500 flex items-center justify-center text-xl">üë®‚Äçüåæ</div>
          <div>
            <h3 className="font-bold">FloraGuard Assistant</h3>
            <p className="text-xs text-emerald-100">AI Plant Pathologist</p>
          </div>
        </div>
      </div>

      <div ref={scrollRef} className="flex-1 overflow-y-auto p-4 space-y-4 bg-slate-50">
        {messages.length === 0 && (
          <div className="text-center py-12 px-6">
            <p className="text-slate-400 text-sm">Ask anything about your plant disease, care tips, or treatment methods!</p>
          </div>
        )}
        {messages.map((msg, i) => (
          <div key={i} className={`flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'}`}>
            <div className={`max-w-[85%] rounded-2xl p-4 shadow-sm ${
              msg.role === 'user' 
                ? 'bg-emerald-600 text-white rounded-tr-none' 
                : 'bg-white text-slate-800 rounded-tl-none border border-slate-100'
            }`}>
              {msg.text && <p className="text-sm leading-relaxed whitespace-pre-wrap">{msg.text}</p>}
            </div>
          </div>
        ))}
        {isLoading && (
          <div className="flex justify-start">
            <div className="bg-white rounded-2xl p-4 shadow-sm border border-slate-100 flex space-x-2 items-center">
              <div className="w-2 h-2 bg-emerald-400 rounded-full animate-bounce"></div>
              <div className="w-2 h-2 bg-emerald-500 rounded-full animate-bounce [animation-delay:0.2s]"></div>
              <div className="w-2 h-2 bg-emerald-600 rounded-full animate-bounce [animation-delay:0.4s]"></div>
            </div>
          </div>
        )}
      </div>

      <form onSubmit={handleSubmit} className="p-4 bg-white border-t border-slate-100 flex items-center space-x-3">
        <input
          type="text"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your question..."
          className="flex-1 bg-slate-100 border-none rounded-xl px-4 py-3 text-sm focus:ring-2 focus:ring-emerald-500 transition-all outline-none"
          disabled={isLoading}
        />
        <button
          type="submit"
          disabled={isLoading || !input.trim()}
          className="bg-emerald-600 hover:bg-emerald-700 disabled:opacity-50 text-white p-3 rounded-xl shadow-md transition-all active:scale-95"
        >
          <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M5 10l7-7m0 0l7 7m-7-7v18" />
          </svg>
        </button>
      </form>
    </div>
  );
};

export default ChatWindow;

import React, { useState, useCallback } from 'react';
import { Message, DiseaseAnalysis } from './types';
import { analyzePlantImage, chatWithBotanist } from './services/geminiService';
import ImageUpload from './components/ImageUpload';
import AnalysisDisplay from './components/AnalysisDisplay';
import ChatWindow from './components/ChatWindow';

const App: React.FC = () => {
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [isChatting, setIsChatting] = useState(false);
  const [currentAnalysis, setCurrentAnalysis] = useState<DiseaseAnalysis | null>(null);
  const [currentImage, setCurrentImage] = useState<string | null>(null);
  const [messages, setMessages] = useState<Message[]>([]);
  const [error, setError] = useState<string | null>(null);

  const handleImageAnalysis = async (base64: string) => {
    setIsAnalyzing(true);
    setError(null);
    setCurrentImage(base64);

    try {
      const result = await analyzePlantImage(base64);
      setCurrentAnalysis(result);

      // Add initial analysis as the start of the chat context
      const initialMessage: Message = {
        role: 'model',
        text: `I've analyzed your ${result.plantName}. It looks like it has ${result.diseaseName}. How else can I help you today?`,
        isAnalysis: true
      };
      setMessages([initialMessage]);
    } catch (err: any) {
      console.error(err);
      setError("I couldn't identify the disease in this image. Please try a clearer photo focusing on the leaf symptoms.");
    } finally {
      setIsAnalyzing(false);
    }
  };

  const handleSendMessage = async (text: string) => {
    const userMsg: Message = { role: 'user', text };
    setMessages(prev => [...prev, userMsg]);
    setIsChatting(true);

    try {
      // Build history for context
      const history = messages.map(m => ({ role: m.role, text: m.text }));
      const responseText = await chatWithBotanist(history, text);

      const botMsg: Message = { role: 'model', text: responseText || "I'm sorry, I couldn't process that request." };
      setMessages(prev => [...prev, botMsg]);
    } catch (err) {
      console.error(err);
      const botMsg: Message = { role: 'model', text: "I'm having trouble connecting right now. Please try again in a moment." };
      setMessages(prev => [...prev, botMsg]);
    } finally {
      setIsChatting(false);
    }
  };

  return (
    <div className="min-h-screen pb-20">
      {/* Header */}
      <header className="bg-white border-b border-emerald-100 sticky top-0 z-50 backdrop-blur-md bg-white/80">
        <div className="max-w-6xl mx-auto px-4 h-20 flex items-center justify-between">
          <div className="flex items-center space-x-2">
            <div className="bg-emerald-600 p-2 rounded-lg text-white font-bold shadow-lg shadow-emerald-200">
              <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M9 12l2 2 4-4m5.618-4.016A11.955 11.955 0 0112 2.944a11.955 11.955 0 01-8.618 3.04A12.02 12.02 0 003 9c0 5.591 3.824 10.29 9 11.622 5.176-1.332 9-6.03 9-11.622 0-1.042-.133-2.052-.382-3.016z" />
              </svg>
            </div>
            <span className="text-2xl font-black text-slate-800 tracking-tight">Flora<span className="text-emerald-600">Guard</span></span>
          </div>
          <div className="hidden md:flex items-center space-x-4 text-sm font-medium text-slate-500">
            <span>Diagnosis</span>
            <span>Treatment</span>
            <span>Prevention</span>
          </div>
        </div>
      </header>

      <main className="max-w-6xl mx-auto px-4 mt-12 space-y-12">
        {/* Welcome Section */}
        {!currentAnalysis && !isAnalyzing && (
          <div className="text-center space-y-4 max-w-2xl mx-auto">
            <h1 className="text-4xl md:text-5xl font-extrabold text-slate-900 leading-tight">Identify Vegetable Plant Diseases <span className="text-emerald-600">Instantly.</span></h1>
            <p className="text-lg text-slate-600">Upload a photo of your infected plant leaf to get expert treatment and prevention advice from our AI botanist.</p>
          </div>
        )}

        {/* Upload Section */}
        <section className="space-y-4">
          <ImageUpload onImageSelected={handleImageAnalysis} disabled={isAnalyzing} />
          {error && (
            <div className="max-w-xl mx-auto p-4 bg-red-50 border border-red-100 rounded-xl text-red-600 text-sm flex items-center">
              <svg className="w-5 h-5 mr-3 flex-shrink-0" fill="currentColor" viewBox="0 0 20 20">
                <path fillRule="evenodd" d="M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zM9 9a1 1 0 000 2v3a1 1 0 001 1h1a1 1 0 100-2v-3a1 1 0 00-1-1H9z" clipRule="evenodd" />
              </svg>
              {error}
            </div>
          )}
        </section>

        {/* Loading State */}
        {isAnalyzing && (
          <div className="flex flex-col items-center justify-center space-y-4 py-12">
            <div className="relative">
              <div className="w-16 h-16 border-4 border-emerald-200 border-t-emerald-600 rounded-full animate-spin"></div>
              <div className="absolute inset-0 flex items-center justify-center">
                <span className="text-xs">üå±</span>
              </div>
            </div>
            <p className="text-slate-600 font-medium animate-pulse">Analyzing symptoms...</p>
          </div>
        )}

        {/* Results & Chat */}
        {currentAnalysis && (
          <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
            <div className="lg:col-span-2 space-y-8">
              <AnalysisDisplay analysis={currentAnalysis} imageUrl={currentImage || undefined} />

              <div className="bg-white p-8 rounded-2xl shadow-sm border border-slate-100">
                <h3 className="text-xl font-bold text-slate-900 mb-4">How it works</h3>
                <p className="text-slate-600 leading-relaxed mb-4">Our model analyzes leaf pigments, patterns, and texture anomalies to detect over 100 types of vegetable plant diseases. Use the assistant on the right for specific follow-up questions.</p>
                <div className="flex space-x-4">
                  <div className="flex items-center text-sm text-emerald-700 font-semibold">
                    <span className="w-2 h-2 bg-emerald-500 rounded-full mr-2"></span>
                    Verified Research
                  </div>
                  <div className="flex items-center text-sm text-emerald-700 font-semibold">
                    <span className="w-2 h-2 bg-emerald-500 rounded-full mr-2"></span>
                    Organic Options
                  </div>
                </div>
              </div>
            </div>

            <div className="lg:col-span-1">
              <div className="sticky top-28">
                <ChatWindow 
                  messages={messages} 
                  onSendMessage={handleSendMessage} 
                  isLoading={isChatting} 
                />
              </div>
            </div>
          </div>
        )}
      </main>

      {/* Footer Branding */}
      {!currentAnalysis && !isAnalyzing && (
        <footer className="max-w-6xl mx-auto px-4 mt-20 text-center border-t border-slate-100 pt-10">
          <div className="grid grid-cols-1 md:grid-cols-3 gap-8 text-left">
            <div className="space-y-3">
              <h4 className="font-bold text-slate-800">Expert Diagnosis</h4>
              <p className="text-sm text-slate-500">Powered by the latest plant pathology datasets for high accuracy.</p>
            </div>
            <div className="space-y-3">
              <h4 className="font-bold text-slate-800">Holistic Care</h4>
              <p className="text-sm text-slate-500">Combining organic remedies with precise chemical advice.</p>
            </div>
            <div className="space-y-3">
              <h4 className="font-bold text-slate-800">Preventive Advice</h4>
              <p className="text-sm text-slate-500">Learn how to shield your entire garden from future outbreaks.</p>
            </div>
          </div>
          <p className="mt-12 text-slate-400 text-xs uppercase tracking-widest font-bold">FloraGuard AI ‚Ä¢ 2024</p>
        </footer>
      )}
    </div>
  );
};

export default App;

