  role: 'user' | 'model';
  text: string;
  image?: string;
  isAnalysis?: boolean;
}

export interface DiseaseAnalysis {
  id: string;
  timestamp: number;
  plantName: string;
  diseaseName: string;
  severity: string;
  symptoms: string[];
  cures: {
    organic: string[];
    chemical: string[];
  };
  prevention: string[];
