                  <span className="w-2 h-2 bg-emerald-500 rounded-full mr-2"></span>
                  Organic Options
                </div>
              </div>
            </div>
          </div>

          <div className="lg:col-span-1">
            <div className="sticky top-6">
              <ChatWindow 
                messages={messages} 
                onSendMessage={handleSendMessage} 
                isLoading={isChatting} 
              />
            </div>
          </div>
        </div>
      )}
    </div>
  );

  const renderHistory = () => (
    <div className="space-y-8 animate-in fade-in duration-500 py-10">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-slate-900">Analysis History</h1>
          <p className="text-slate-500 mt-1">Review your past plant health checks.</p>
        </div>
        <button 
          onClick={() => { setHistory([]); localStorage.removeItem('flora_guard_history'); }}
          className="text-sm text-red-500 hover:text-red-700 font-medium"
        >
          Clear All
        </button>
      </div>

      {history.length === 0 ? (
