                  <span className="w-2 h-2 bg-emerald-500 rounded-full mr-2"></span>
                  Verified Research
                </div>
                <div className="flex items-center text-sm text-emerald-700 font-semibold bg-emerald-50 px-3 py-1 rounded-full">
                  <span className="w-2 h-2 bg-emerald-500 rounded-full mr-2"></span>
                  Organic Options
                </div>
              </div>
            </div>
          </div>

          <div className="lg:col-span-1">
            <div className="sticky top-6">
              <ChatWindow 
                messages={messages} 
                onSendMessage={handleSendMessage}
                onImageUpload={handleImageAnalysis}
                isLoading={isChatting || isAnalyzing} 
              />
            </div>
          </div>
        </div>
      )}
    </div>
  );

  const renderHistory = () => (
    <div className="space-y-8 animate-in fade-in duration-500 py-10">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-slate-900">Analysis History</h1>
          <p className="text-slate-500 mt-1">Review your past plant health checks.</p>
        </div>
        <button 
          onClick={() => { setHistory([]); localStorage.removeItem('flora_guard_history'); }}
          className="text-sm text-red-500 hover:text-red-700 font-medium"
        >
          Clear All
        </button>
      </div>

      {history.length === 0 ? (
        <div className="text-center py-20 bg-white rounded-3xl border border-dashed border-slate-200">
          <p className="text-slate-400">No history found. Start your first analysis!</p>
