      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
          {history.map((item) => (
            <div 
              key={item.id} 
              className="bg-white rounded-2xl overflow-hidden shadow-sm border border-slate-100 hover:shadow-md transition-shadow cursor-pointer group"
              onClick={() => {
                setCurrentAnalysis(item);
                setCurrentImage(item.imageUrl || null);
                setView('home');
              }}
            >
              <div className="h-40 bg-slate-100 relative overflow-hidden">
                {item.imageUrl ? (
                  <img src={`data:image/jpeg;base64,${item.imageUrl}`} className="w-full h-full object-cover transition-transform group-hover:scale-105" alt={item.diseaseName} />
                ) : (
                  <div className="w-full h-full flex items-center justify-center text-slate-300">No Image</div>
                )}
                <div className="absolute top-2 right-2 px-2 py-1 bg-white/90 backdrop-blur rounded text-[10px] font-bold uppercase">
                  {new Date(item.timestamp).toLocaleDateString()}
                </div>
              </div>
              <div className="p-4">
                <p className="text-xs font-bold text-emerald-600 uppercase tracking-widest">{item.plantName}</p>
                <h3 className="font-bold text-slate-900 mt-1 truncate">{item.diseaseName}</h3>
                <div className="mt-4 flex items-center justify-between text-xs text-slate-400">
                  <span>View Details</span>
                  <svg className="w-4 h-4 text-emerald-500" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M17 8l4 4m0 0l-4 4m4-4H3" />
                  </svg>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
