              <div className="h-full bg-emerald-500" style={{ width: '28%' }}></div>
            </div>
          </div>

          <div className="space-y-4">
            <h4 className="text-xs font-bold text-slate-400 uppercase tracking-widest">Preferences</h4>
            <div className="flex items-center justify-between">
              <span className="text-sm text-slate-700 font-medium">Auto-save Analysis</span>
              <div className="w-10 h-5 bg-emerald-600 rounded-full relative">
                <div className="absolute right-1 top-1 w-3 h-3 bg-white rounded-full"></div>
              </div>
            </div>
            <div className="flex items-center justify-between">
              <span className="text-sm text-slate-700 font-medium">Notification Alerts</span>
