export type AppView = 'home' | 'history' | 'account';

export interface Message {
  role: 'user' | 'model';
  text: string;
  image?: string;
  isAnalysis?: boolean;
  analysis?: DiseaseAnalysis;
}

export interface DiseaseAnalysis {
  id: string;
  timestamp: number;
  plantName: string;
  diseaseName: string;
  severity: string;
  symptoms: string[];
  cures: {
    organic: string[];
    chemical: string[];
  };
  prevention: string[];
  imageUrl?: string;
}

export interface ChatState {
  messages: Message[];
  isAnalyzing: boolean;
  error: string | null;
}

import React, { useState, useEffect } from 'react';
import { Message, DiseaseAnalysis, AppView } from './types';
import { analyzePlantImage, chatWithBotanist } from './services/geminiService';
import ChatWindow from './components/ChatWindow';
import Sidebar from './components/Sidebar';

const App: React.FC = () => {
  const [view, setView] = useState<AppView>('home');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [isChatting, setIsChatting] = useState(false);
  const [messages, setMessages] = useState<Message[]>([]);
  const [history, setHistory] = useState<DiseaseAnalysis[]>([]);

  // Load history from local storage on mount
  useEffect(() => {
    const savedHistory = localStorage.getItem('flora_guard_history');
    if (savedHistory) {
      try {
        setHistory(JSON.parse(savedHistory));
      } catch (e) {
        console.error("Failed to parse history", e);
      }
    }
  }, []);

  const handleImageAnalysis = async (base64: string) => {
    setIsAnalyzing(true);

    // We no longer add the "Uploaded an image..." placeholder message to the chat
    // This cleans up the UI as requested in the "remove this part" instruction

    try {
      const resultRaw = await analyzePlantImage(base64);
      const result: DiseaseAnalysis = {
        ...resultRaw,
        id: crypto.randomUUID(),
        timestamp: Date.now(),
        imageUrl: base64
      };

      // Save to history
      const updatedHistory = [result, ...history];
      setHistory(updatedHistory);
      localStorage.setItem('flora_guard_history', JSON.stringify(updatedHistory));

      const botMsg: Message = {
        role: 'model',
        text: `I've completed the diagnostic scan for your plant.`,
        isAnalysis: true,
        analysis: result,
        image: base64 // Keeping the image ref in the message
      };
      setMessages(prev => [...prev, botMsg]);
    } catch (err: any) {
      console.error(err);
      const errorMsg: Message = { 
        role: 'model', 
        text: "I'm sorry, I couldn't identify the disease in that photo. Could you try uploading a clearer image of the affected area?" 
      };
      setMessages(prev => [...prev, errorMsg]);
    } finally {
      setIsAnalyzing(false);
    }
  };

  const handleSendMessage = async (text: string) => {
    const userMsg: Message = { role: 'user', text };
    setMessages(prev => [...prev, userMsg]);
