      const botMsg: Message = { role: 'model', text: responseText || "I'm sorry, I couldn't process that request." };
      setMessages(prev => [...prev, botMsg]);
    } catch (err) {
      console.error(err);
      const botMsg: Message = { role: 'model', text: "I'm having trouble connecting right now. Please try again in a moment." };
