import { GoogleGenAI, GenerateContentResponse, Type } from "@google/genai";
import { DiseaseAnalysis } from "../types";

const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

const SYSTEM_INSTRUCTION = `You are an expert vegetable plant pathologist and agronomist. 
Your goal is to identify diseases in vegetable plant leaves accurately and provide clear, professional advice on treatment and prevention.
When analyzing an image, focus on the specific visual symptoms. 
Provide organic and chemical solutions. 
For chemical treatments, provide a valid search or direct purchase link (e.g., Google Shopping or a major agricultural retailer) so the user can buy the product.
Always prioritize safety and environmental responsibility.`;

export const analyzePlantImage = async (base64Image: string): Promise<DiseaseAnalysis> => {
  const model = 'gemini-3-flash-preview';

  const prompt = `Analyze this vegetable plant leaf image. Identify:
1. The common name of the plant.
2. The specific disease name (if any).
3. Visual symptoms observed.
4. Organic cures.
5. Recommended chemical/pesticide treatments.
6. For each chemical treatment, provide a direct search/purchase URL (e.g. Amazon, Google Shopping, or a specialized site).
7. Preventive measures to stop the spread.

Return the data strictly in JSON format matching this schema:
{
  "plantName": string,
  "diseaseName": string,
  "severity": "Low" | "Medium" | "High",
  "symptoms": string[],
  "cures": {
    "organic": string[],
    "chemical": string[]
  },
  "purchaseLinks": [
    { "pesticideName": string, "url": string }
  ],
  "prevention": string[]
}`;

  const response = await ai.models.generateContent({
    model,
    contents: {
      parts: [
        { inlineData: { mimeType: 'image/jpeg', data: base64Image } },
        { text: prompt }
      ]
    },
    config: {
      systemInstruction: SYSTEM_INSTRUCTION,
      responseMimeType: "application/json"
    }
  });

  const text = response.text;
  if (!text) throw new Error("No response from AI");

  try {
    return JSON.parse(text) as DiseaseAnalysis;
  } catch (err) {
    console.error("Failed to parse AI response:", text);
    throw new Error("Invalid response format from AI");
  }
};

export const chatWithBotanist = async (history: { role: 'user' | 'model', text: string }[], newMessage: string) => {
  const chat = ai.chats.create({
    model: 'gemini-3-flash-preview',
    config: {
      systemInstruction: SYSTEM_INSTRUCTION,
    }
  });

  const response = await chat.sendMessage({ message: newMessage });
  return response.text;
};

import React from 'react';
import { DiseaseAnalysis } from '../types';

interface AnalysisDisplayProps {
  analysis: DiseaseAnalysis;
  imageUrl?: string;
}

const AnalysisDisplay: React.FC<AnalysisDisplayProps> = ({ analysis, imageUrl }) => {
  const severityColors = {
    Low: 'bg-yellow-100 text-yellow-700',
    Medium: 'bg-orange-100 text-orange-700',
    High: 'bg-red-100 text-red-700',
  };

  const downloadReport = () => {
    const reportContent = `
FLORAGUARD AI - PLANT HEALTH REPORT
Generated on: ${new Date(analysis.timestamp).toLocaleString()}
--------------------------------------------------

PLANT: ${analysis.plantName}
DIAGNOSIS: ${analysis.diseaseName}
SEVERITY: ${analysis.severity}

SYMPTOMS:
${analysis.symptoms.map(s => `- ${s}`).join('\n')}

TREATMENT - ORGANIC SOLUTIONS:
${analysis.cures.organic.map(c => `- ${c}`).join('\n')}

TREATMENT - CHEMICAL/PESTICIDE SOLUTIONS:
${analysis.cures.chemical.map(c => `- ${c}`).join('\n')}

PREVENTION MEASURES:
${analysis.prevention.map(p => `- ${p}`).join('\n')}

--------------------------------------------------
This report was generated by FloraGuard AI Assistant.
Always consult with a local agricultural extension officer for regional-specific advice.
    `.trim();

    const blob = new Blob([reportContent], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `FloraGuard_Report_${analysis.plantName.replace(/\s+/g, '_')}.txt`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
