import { GoogleGenAI, GenerateContentResponse, Type } from "@google/genai";
import { DiseaseAnalysis } from "../types";

const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

const SYSTEM_INSTRUCTION = `You are an expert vegetable plant pathologist, entomologist, and agronomist. 
Your goal is to identify diseases, pests, insects, and nutritional deficiencies in vegetable plant leaves accurately.
Provide clear, professional advice on:
1. Identification: Name the specific disease or pest (e.g., Aphids, Spider Mites, Early Blight).
2. Symptoms/Signs: Detail what is seen (e.g., leaf curling, sticky residue, holes, spots).
3. Organic Cures: Natural ways to treat the issue.
4. Chemical Solutions: Pesticides or fungicides that are effective.
5. Prevention: How to stop it from returning.
6. Purchase Links: For every chemical treatment, provide a direct search or purchase URL (e.g., Amazon, Google Shopping, or a major agricultural retailer).

Always prioritize safety, environmental responsibility, and accurate identification of both diseases and harmful bugs.`;

export const analyzePlantImage = async (base64Image: string): Promise<DiseaseAnalysis> => {
  const model = 'gemini-3-flash-preview';

  const prompt = `Analyze this vegetable plant leaf image. Identify if it has a disease or a pest/bug infestation. Identify:
1. The common name of the plant.
2. The specific identification name (e.g., the disease name or the bug/pest name).
3. Visual symptoms or signs of pests (like webbing, eggs, or bite marks) observed.
4. Organic cures or natural predators/deterrents.
5. Recommended chemical/pesticide treatments.
6. For each chemical treatment, provide a direct search/purchase URL.
7. Preventive measures to stop the spread or return.

Return the data strictly in JSON format matching this schema:
{
  "plantName": string,
  "diseaseName": string, 
  "severity": "Low" | "Medium" | "High",
  "symptoms": string[],
  "cures": {
    "organic": string[],
    "chemical": string[]
  },
  "purchaseLinks": [
    { "pesticideName": string, "url": string }
  ],
  "prevention": string[]
}
Note: "diseaseName" in the JSON should be the name of the pest if it's a bug infestation.`;

  const response = await ai.models.generateContent({
    model,
    contents: {
      parts: [
        { inlineData: { mimeType: 'image/jpeg', data: base64Image } },
        { text: prompt }
      ]
    },
    config: {
      systemInstruction: SYSTEM_INSTRUCTION,
      responseMimeType: "application/json"
    }
  });

  const text = response.text;
  if (!text) throw new Error("No response from AI");

  try {
    return JSON.parse(text) as DiseaseAnalysis;
  } catch (err) {
    console.error("Failed to parse AI response:", text);
    throw new Error("Invalid response format from AI");
  }
};

export const chatWithBotanist = async (history: { role: 'user' | 'model', text: string }[], newMessage: string) => {
  const chat = ai.chats.create({
    model: 'gemini-3-flash-preview',
    config: {
      systemInstruction: SYSTEM_INSTRUCTION,
    }
  });

  const response = await chat.sendMessage({ message: newMessage });
  return response.text;
};

