import React, { useState, useRef, useEffect } from 'react';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';
import { Message } from '../types';
import AnalysisDisplay from './AnalysisDisplay';

interface ChatWindowProps {
  messages: Message[];
  onSendMessage: (text: string) => void;
  onImageUpload?: (base64: string) => void;
  isLoading: boolean;
}

const ChatWindow: React.FC<ChatWindowProps> = ({ messages, onSendMessage, onImageUpload, isLoading }) => {
  const [input, setInput] = useState('');
  const scrollRef = useRef<HTMLDivElement>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  useEffect(() => {
    if (scrollRef.current) {
      scrollRef.current.scrollTop = scrollRef.current.scrollHeight;
    }
  }, [messages, isLoading]);

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    if (input.trim() && !isLoading) {
      onSendMessage(input.trim());
      setInput('');
    }
  };

  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file && onImageUpload) {
      const reader = new FileReader();
      reader.onloadend = () => {
        const base64String = (reader.result as string).split(',')[1];
        onImageUpload(base64String);
      };
      reader.readAsDataURL(file);
    }
    if (fileInputRef.current) fileInputRef.current.value = '';
  };

  const triggerUpload = () => {
    fileInputRef.current?.click();
  };

  return (
    <div className="flex flex-col h-[700px] bg-white rounded-3xl shadow-2xl border border-slate-200/60 overflow-hidden">
      {/* Header */}
      <div className="p-4 bg-emerald-600 text-white flex items-center justify-between shadow-md z-10">
        <div className="flex items-center space-x-3">
          <div className="w-10 h-10 rounded-full bg-emerald-500/50 flex items-center justify-center text-xl border border-white/20">üë®‚Äçüåæ</div>
          <div>
            <h3 className="font-bold text-white text-sm">FloraGuard Assistant</h3>
            <div className="flex items-center space-x-1">
              <span className="w-2 h-2 bg-emerald-300 rounded-full animate-pulse"></span>
              <p className="text-[10px] text-emerald-100 font-medium uppercase tracking-wider">Online ‚Ä¢ AI Plant Pathologist & Entomologist</p>
            </div>
          </div>
        </div>
      </div>

      {/* Messages Area */}
      <div ref={scrollRef} className="flex-1 overflow-y-auto p-4 md:p-6 space-y-6 bg-slate-50/80 scroll-smooth">
        {messages.length === 0 && (
          <div className="flex flex-col items-center justify-center h-full text-center space-y-4 opacity-50">
            <div className="w-16 h-16 bg-slate-200 rounded-full flex items-center justify-center text-3xl">ü™≤</div>
            <div>
              <p className="text-slate-600 font-bold">Expert Bug & Disease Identification</p>
              <p className="text-slate-400 text-xs max-w-[200px] mx-auto">Upload a photo to detect pests like aphids and mites, or diseases like blight and rust.</p>
            </div>
          </div>
        )}

        {messages.map((msg, i) => (
          <div key={i} className={`flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'} animate-in slide-in-from-bottom-2 duration-300`}>
            <div className={`group relative max-w-[95%] sm:max-w-[85%] rounded-3xl overflow-hidden shadow-sm ${
              msg.role === 'user' 
                ? 'bg-emerald-600 text-white rounded-tr-none' 
                : 'bg-white text-slate-800 rounded-tl-none border border-slate-200/60'
            }`}>
              {/* Rich Analysis Result inside Chat Bubble */}
              {msg.analysis ? (
                <div className="w-full">
                   <AnalysisDisplay analysis={msg.analysis} imageUrl={msg.analysis.imageUrl} />
                </div>
              ) : (
                <div className="p-4">
                  {msg.image && !msg.analysis && (
                    <div className="mb-3 rounded-xl overflow-hidden border border-slate-100 shadow-inner">
                       <img src={`data:image/jpeg;base64,${msg.image}`} alt="Uploaded" className="max-h-60 w-full object-cover" />
                    </div>
                  )}
                  {msg.text && (
                    <div className={`text-sm leading-relaxed prose prose-sm max-w-none ${msg.role === 'user' ? 'prose-invert' : 'prose-emerald'}`}>
                      <ReactMarkdown 
                        remarkPlugins={[remarkGfm]}
                        components={{
                          strong: ({node, ...props}) => <strong className="font-bold text-inherit underline decoration-emerald-500/30" {...props} />,
                          p: ({node, ...props}) => <p className="mb-2 last:mb-0" {...props} />,
                        }}
                      >
                        {msg.text}
                      </ReactMarkdown>
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>
        ))}

        {isLoading && (
          <div className="flex justify-start">
            <div className="bg-white rounded-2xl px-4 py-3 shadow-sm border border-slate-200/60 flex space-x-2 items-center">
              <div className="w-1.5 h-1.5 bg-emerald-400 rounded-full animate-bounce"></div>
              <div className="w-1.5 h-1.5 bg-emerald-500 rounded-full animate-bounce [animation-delay:0.2s]"></div>
              <div className="w-1.5 h-1.5 bg-emerald-600 rounded-full animate-bounce [animation-delay:0.4s]"></div>
            </div>
          </div>
        )}
      </div>

      {/* Input Section */}
      <form onSubmit={handleSubmit} className="p-4 bg-white border-t border-slate-200/60 flex items-center space-x-2">
        <input 
          type="file" 
          ref={fileInputRef} 
          className="hidden" 
          accept="image/*" 
          onChange={handleFileChange}
          disabled={isLoading}
        />
        <button
          type="button"
          onClick={triggerUpload}
          disabled={isLoading}
          className="p-3 text-slate-400 hover:text-emerald-600 hover:bg-emerald-50 rounded-2xl transition-all disabled:opacity-50 border border-transparent hover:border-emerald-100"
          title="Upload Plant/Bug Image"
        >
          <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M3 9a2 2 0 012-2h.93a2 2 0 001.664-.89l.812-1.22A2 2 0 0110.07 4h3.86a2 2 0 011.664.89l.812 1.22A2 2 0 0018.07 7H19a2 2 0 012 2v9a2 2 0 01-2 2H5a2 2 0 01-2-2V9z" />
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 13a3 3 0 11-6 0 3 3 0 016 0z" />
          </svg>
        </button>
        <div className="flex-1 relative">
          <input
            type="text"
            value={input}
            onChange={(e) => setInput(e.target.value)}
            placeholder="Describe bugs or ask a question..."
            className="w-full bg-slate-100 border border-slate-200 rounded-2xl px-4 py-3.5 text-sm text-slate-900 placeholder-slate-500 focus:ring-2 focus:ring-emerald-500/20 focus:border-emerald-500 focus:bg-white transition-all outline-none"
            disabled={isLoading}
          />
        </div>
        <button
          type="submit"
          disabled={isLoading || !input.trim()}
          className="bg-emerald-600 hover:bg-emerald-700 disabled:opacity-50 text-white p-3.5 rounded-2xl shadow-lg shadow-emerald-600/20 transition-all active:scale-95 flex items-center justify-center group"
        >
          <svg className="w-5 h-5 group-hover:translate-x-0.5 group-hover:-translate-y-0.5 transition-transform" fill="none" stroke="currentColor" viewBox="0 0 24 24">
            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M12 19l9 2-9-18-9 18 9-2zm0 0v-8" />
          </svg>
        </button>
      </form>
    </div>
  );
};

export default ChatWindow;

import { GoogleGenAI } from "@google/genai";
import { DiseaseAnalysis } from "../types";

const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

const SYSTEM_INSTRUCTION = `You are an expert vegetable plant pathologist, entomologist, and agronomist. 
Your goal is to identify diseases, pests, insects, and nutritional deficiencies in vegetable plant leaves accurately.
Provide clear, professional advice on:
1. Identification: Name the specific disease or pest (e.g., Aphids, Spider Mites, Early Blight).
2. Symptoms/Signs: Detail what is seen (e.g., leaf curling, sticky residue, holes, spots).
3. Organic Cures: Natural ways to treat the issue.
4. Chemical Solutions: Specific pesticides or fungicides that are effective.
5. Prevention: How to stop it from returning.
6. MANDATORY Purchase Links: For EVERY chemical treatment listed, you MUST provide a direct search or purchase URL. If a direct product page is not available, use a Google Shopping or Amazon search query URL for that specific pesticide name.

Always prioritize safety, environmental responsibility, and accurate identification of both diseases and harmful bugs. Ensure the JSON purchaseLinks array contains exactly one link for every item in the cures.chemical array.`;

export const analyzePlantImage = async (base64Image: string): Promise<DiseaseAnalysis> => {
  const model = 'gemini-3-flash-preview';

  const prompt = `Analyze this vegetable plant leaf image. Identify if it has a disease or a pest/bug infestation. Identify:
1. The common name of the plant.
2. The specific identification name (e.g., the disease name or the bug/pest name).
3. Visual symptoms or signs of pests (like webbing, holes, or spots) observed.
4. Organic cures or natural deterrents.
5. Recommended chemical/pesticide treatments.
6. MANDATORY: For every chemical pesticide mentioned, provide a direct search or purchase URL.
7. Preventive measures to stop the return.

Return the data strictly in JSON format matching this schema:
{
  "plantName": string,
  "diseaseName": string, 
  "severity": "Low" | "Medium" | "High",
  "symptoms": string[],
  "cures": {
    "organic": string[],
    "chemical": string[]
  },
  "purchaseLinks": [
    { "pesticideName": string, "url": string }
  ],
  "prevention": string[]
}
Note: "diseaseName" should be the name of the pest if it's a bug infestation. Ensure EVERY chemical listed in cures.chemical has a corresponding entry in purchaseLinks.`;

  const response = await ai.models.generateContent({
    model,
    contents: {
      parts: [
        { inlineData: { mimeType: 'image/jpeg', data: base64Image } },
        { text: prompt }
      ]
    },
    config: {
      systemInstruction: SYSTEM_INSTRUCTION,
      responseMimeType: "application/json"
    }
  });

  const text = response.text;
  if (!text) throw new Error("No response from AI");

  try {
    return JSON.parse(text) as DiseaseAnalysis;
  } catch (err) {
    console.error("Failed to parse AI response:", text);
    throw new Error("Invalid response format from AI");
  }
};

export const chatWithBotanist = async (history: { role: 'user' | 'model', text: string }[], newMessage: string) => {
  const chat = ai.chats.create({
    model: 'gemini-3-flash-preview',
    config: {
      systemInstruction: SYSTEM_INSTRUCTION,
    }
  });

  const response = await chat.sendMessage({ message: newMessage });
  return response.text;
};

import React from 'react';
