  imageUrl?: string;
}

const AnalysisDisplay: React.FC<AnalysisDisplayProps> = ({ analysis, imageUrl }) => {
  const severityColors = {
    Low: 'bg-yellow-100 text-yellow-700',
    Medium: 'bg-orange-100 text-orange-700',
    High: 'bg-red-100 text-red-700',
  };

  const downloadReport = () => {
    const reportContent = `
FLORAGUARD AI - PLANT HEALTH REPORT
Generated on: ${new Date(analysis.timestamp).toLocaleString()}
--------------------------------------------------

PLANT: ${analysis.plantName}
IDENTIFICATION: ${analysis.diseaseName}
SEVERITY: ${analysis.severity}

SYMPTOMS & SIGNS:
${analysis.symptoms.map(s => `- ${s}`).join('\n')}

TREATMENT - ORGANIC SOLUTIONS:
${analysis.cures.organic.map(c => `- ${c}`).join('\n')}

TREATMENT - CHEMICAL/PESTICIDE SOLUTIONS:
${analysis.cures.chemical.map(c => `- ${c}`).join('\n')}

PREVENTION MEASURES:
${analysis.prevention.map(p => `- ${p}`).join('\n')}

--------------------------------------------------
This report was generated by FloraGuard AI Assistant.
Always consult with a local agricultural extension officer for regional-specific advice.
    `.trim();

    const blob = new Blob([reportContent], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `FloraGuard_Report_${analysis.plantName.replace(/\s+/g, '_')}.txt`;
    document.body.appendChild(link);
    link.click();
    document.body.removeChild(link);
    URL.revokeObjectURL(url);
  };

  const getPurchaseUrl = (chemical: string) => {
    // Attempt to find the link provided by AI
    const pLink = analysis.purchaseLinks?.find(l => 
      l.pesticideName.toLowerCase().includes(chemical.toLowerCase()) || 
      chemical.toLowerCase().includes(l.pesticideName.toLowerCase())
    );

    // If AI provided a link, use it. Otherwise, create a guaranteed fallback link.
    if (pLink?.url) return pLink.url;

    // Fallback: Google Shopping search link
    return `https://www.google.com/search?tbm=shop&q=${encodeURIComponent(chemical + ' pesticide for ' + analysis.plantName)}`;
  };

