const ai = new GoogleGenAI({ apiKey: process.env.API_KEY });

const SYSTEM_INSTRUCTION = `You are an expert vegetable plant pathologist, entomologist, and agronomist. 
Your goal is to identify diseases, pests, insects, and nutritional deficiencies in vegetable plant leaves accurately.
Provide clear, professional advice on:
1. Identification: Name the specific disease or pest (e.g., Aphids, Spider Mites, Early Blight).
2. Symptoms/Signs: Detail what is seen (e.g., leaf curling, sticky residue, holes, spots).
3. Organic Cures: Natural ways to treat the issue.
4. Chemical Solutions: Specific pesticides or fungicides that are effective.
5. Prevention: How to stop it from returning.
6. MANDATORY Purchase Links: For EVERY chemical treatment listed, you MUST provide a direct search or purchase URL. If a direct product page is not available, use a Google Shopping or Amazon search query URL for that specific pesticide name.

Always prioritize safety, environmental responsibility, and accurate identification of both diseases and harmful bugs. Ensure the JSON purchaseLinks array contains exactly one link for every item in the cures.chemical array.`;

const GARDENING_MASTER_INSTRUCTION = `You are the "Garden Master," an expert horticulturist specializing in Indian garden plants and home gardening. 
When a user provides a plant name, you MUST provide a comprehensive guide in the following EXACT structure:

### ü™¥ Potting Mixture Ratio
- **Soil:** [Percentage]%
- **Sand:** [Percentage]%
- **Organic Manure/Compost:** [Percentage]%
- *Note:* [Specific soil type advice if any]

### üíß Watering Needs
[Describe frequency and quantity based on Indian seasons]

### ‚òÄÔ∏è Sunlight Requirements
[Describe duration and intensity (Direct/Indirect)]

### üêõ Possible Diseases & Pests
[List common pests and diseases affecting this specific plant]

### üå∏ Flowering Season
[Specify months or seasons, or state "Year-round" or "Not applicable" for non-flowering]

### üß™ Fertilizer Time Period
[When and how often to give fertilizers for best results]

### ‚úÇÔ∏è Maintenance: Repotting & Pruning
- **Repotting:** [When to change the pot]
- **Pruning:** [Best months/signs for pruning]

Keep the tone encouraging and professional. If the user asks something else, answer helpfully but prioritize this structure for plant names.`;

export const analyzePlantImage = async (base64Image: string): Promise<DiseaseAnalysis> => {
  const model = 'gemini-3-flash-preview';

  const prompt = `Analyze this vegetable plant leaf image. Identify if it has a disease or a pest/bug infestation. Identify:
1. The common name of the plant.
2. The specific identification name (e.g., the disease name or the bug/pest name).
3. Visual symptoms or signs of pests (like webbing, holes, or spots) observed.
4. Organic cures or natural deterrents.
5. Recommended chemical/pesticide treatments.
6. MANDATORY: For every chemical pesticide mentioned, provide a direct search or purchase URL.
7. Preventive measures to stop the return.

Return the data strictly in JSON format matching this schema:
{
  "plantName": string,
  "diseaseName": string, 
  "severity": "Low" | "Medium" | "High",
  "symptoms": string[],
  "cures": {
    "organic": string[],
    "chemical": string[]
  },
  "purchaseLinks": [
    { "pesticideName": string, "url": string }
  ],
  "prevention": string[]
}
Note: "diseaseName" should be the name of the pest if it's a bug infestation. Ensure EVERY chemical listed in cures.chemical has a corresponding entry in purchaseLinks.`;

  const response = await ai.models.generateContent({
    model,
    contents: {
      parts: [
        { inlineData: { mimeType: 'image/jpeg', data: base64Image } },
        { text: prompt }
      ]
    },
    config: {
      systemInstruction: SYSTEM_INSTRUCTION,
      responseMimeType: "application/json"
    }
  });

  const text = response.text;
  if (!text) throw new Error("No response from AI");

  try {
    return JSON.parse(text) as DiseaseAnalysis;
  } catch (err) {
    console.error("Failed to parse AI response:", text);
    throw new Error("Invalid response format from AI");
  }
};

export const chatWithBotanist = async (history: { role: 'user' | 'model', text: string }[], newMessage: string) => {
  const chat = ai.chats.create({
    model: 'gemini-3-flash-preview',
    config: {
      systemInstruction: SYSTEM_INSTRUCTION,
    }
  });

  const response = await chat.sendMessage({ message: newMessage });
  return response.text;
};

export const chatWithGardenMaster = async (history: { role: 'user' | 'model', text: string }[], newMessage: string) => {
  const chat = ai.chats.create({
    model: 'gemini-3-flash-preview',
    config: {
      systemInstruction: GARDENING_MASTER_INSTRUCTION,
    }
  });

  const response = await chat.sendMessage({ message: newMessage });
  return response.text;
};

import React, { useState, useRef, useEffect } from 'react';
import { Message } from '../types';
import { chatWithGardenMaster } from '../services/geminiService';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';

const GardenCare: React.FC = () => {
  const [chatMessages, setChatMessages] = useState<Message[]>([
    { 
      role: 'model', 
      text: "Namaste! I am your Garden Master. **Just type a plant name** (e.g., *Hibiscus*, *Rose*, *Tulsi*) and I will provide a complete guide on potting mix, watering, pests, and maintenance schedules." 
    }
  ]);
  const [chatInput, setChatInput] = useState('');
  const [isChatLoading, setIsChatLoading] = useState(false);
  const chatEndRef = useRef<HTMLDivElement>(null);

  useEffect(() => {
    chatEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [chatMessages]);

  const handleChatSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    if (!chatInput.trim() || isChatLoading) return;

    const userMsg: Message = { role: 'user', text: chatInput };
    setChatMessages(prev => [...prev, userMsg]);
    setChatInput('');
    setIsChatLoading(true);

    try {
      const history = chatMessages.map(m => ({ role: m.role, text: m.text }));
      const reply = await chatWithGardenMaster(history, userMsg.text);
      setChatMessages(prev => [...prev, { role: 'model', text: reply || "I couldn't generate a guide for that plant. Please check the spelling!" }]);
    } catch (err) {
      setChatMessages(prev => [...prev, { role: 'model', text: "I'm having trouble connecting to my garden wisdom. Please try again later." }]);
    } finally {
      setIsChatLoading(false);
    }
  };

  return (
    <div className="space-y-12 animate-in fade-in duration-500 py-10 pb-20">
      {/* 1. Header Section */}
      <div className="flex flex-col md:flex-row md:items-center justify-between gap-6">
        <div>
          <h1 className="text-3xl font-black text-slate-900 tracking-tight">FloraGuard Garden Care</h1>
          <p className="text-slate-500 mt-1">Get comprehensive soil, water, and pest details for any plant.</p>
        </div>
      </div>

      {/* 2. Garden Master Chat Section */}
      <div className="max-w-4xl mx-auto">
        <div className="bg-slate-900 rounded-[2.5rem] p-1 shadow-2xl overflow-hidden border border-emerald-500/20">
          <div className="bg-slate-800 p-6 rounded-t-[2.2rem] flex items-center justify-between border-b border-white/5">
            <div className="flex items-center space-x-4">
               <div className="w-14 h-14 rounded-2xl bg-emerald-500/20 border border-emerald-500/30 flex items-center justify-center text-3xl shadow-inner">
                 üßë‚Äçüåæ
               </div>
               <div>
                 <h2 className="text-xl font-black text-white">Plant Care Guide</h2>
                 <p className="text-emerald-400 text-[10px] font-bold uppercase tracking-widest">AI Horticulture Expert ‚Ä¢ Online</p>
               </div>
            </div>
            <div className="hidden lg:flex space-x-2">
               <span className="px-3 py-1 bg-white/5 text-white/50 text-[10px] font-bold rounded-full">Potting Ratios</span>
               <span className="px-3 py-1 bg-white/5 text-white/50 text-[10px] font-bold rounded-full">Maintenance</span>
               <span className="px-3 py-1 bg-white/5 text-white/50 text-[10px] font-bold rounded-full">Pest Control</span>
            </div>
          </div>

          <div className="bg-slate-900 h-[500px] overflow-y-auto p-6 space-y-6 flex flex-col scroll-smooth">
             {chatMessages.map((msg, i) => (
               <div key={i} className={`flex ${msg.role === 'user' ? 'justify-end' : 'justify-start'} animate-in slide-in-from-bottom-2`}>
                 <div className={`max-w-[85%] rounded-3xl p-5 ${
                   msg.role === 'user' 
                    ? 'bg-emerald-600 text-white rounded-tr-none shadow-lg shadow-emerald-900/20' 
                    : 'bg-white/10 text-slate-100 rounded-tl-none border border-white/5'
                 }`}>
                    <div className="text-sm prose prose-invert prose-sm max-w-none">
                       <ReactMarkdown remarkPlugins={[remarkGfm]}>{msg.text}</ReactMarkdown>
                    </div>
                 </div>
               </div>
             ))}
             {isChatLoading && (
               <div className="flex justify-start">
                  <div className="bg-white/5 rounded-2xl px-4 py-3 flex space-x-2">
                    <div className="w-2 h-2 bg-emerald-500 rounded-full animate-bounce"></div>
                    <div className="w-2 h-2 bg-emerald-500 rounded-full animate-bounce [animation-delay:0.2s]"></div>
                    <div className="w-2 h-2 bg-emerald-500 rounded-full animate-bounce [animation-delay:0.4s]"></div>
                  </div>
               </div>
             )}
             <div ref={chatEndRef} />
          </div>

          <form onSubmit={handleChatSubmit} className="p-6 bg-slate-800 rounded-b-[2.2rem] flex items-center space-x-3">
             <input 
               type="text" 
               value={chatInput}
               onChange={(e) => setChatInput(e.target.value)}
               placeholder="Just type a plant name (e.g. Hibiscus, Rose, Tulsi)..."
               className="flex-1 bg-slate-900/50 border border-white/10 rounded-2xl px-6 py-4 text-sm text-white placeholder-white/30 outline-none focus:ring-2 focus:ring-emerald-500/50 transition-all shadow-inner"
               disabled={isChatLoading}
             />
             <button 
               type="submit"
               disabled={isChatLoading || !chatInput.trim()}
               className="bg-emerald-600 hover:bg-emerald-500 disabled:opacity-50 text-white p-4 rounded-2xl shadow-lg transition-all active:scale-95 flex items-center justify-center"
             >
                <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2.5" d="M14 5l7 7m0 0l-7 7m7-7H3" />
                </svg>
             </button>
          </form>
        </div>
      </div>

      <div className="text-center pt-10">
        <p className="text-slate-400 text-xs font-bold uppercase tracking-[0.2em]"> FloraGuard AI ‚Ä¢ Smart Agriculture & Gardening </p>
      </div>
    </div>
  );
};

export default GardenCare;

