  id: string;
  name: string;
  description: string;
  imageUrl: string;
  pottingMix: {
    soil: number;
    sand: number;
    compost: number;
    others?: string;
  };
  watering: string;
  sunlight: string;
  floweringSeason: string;
}

export interface ChatState {
  messages: Message[];
  isAnalyzing: boolean;
  error: string | null;
}

