import React, { useState, useEffect } from 'react';
import { Message, HistoryItem, AppView } from './types';
import { analyzePlantImage, chatWithBotanist } from './services/geminiService';
import ChatWindow from './components/ChatWindow';
import Sidebar from './components/Sidebar';
import GardenCare from './components/GardenCare';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';

const App: React.FC = () => {
  const [view, setView] = useState<AppView>('home');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [isChatting, setIsChatting] = useState(false);
  const [messages, setMessages] = useState<Message[]>([]);
  const [history, setHistory] = useState<HistoryItem[]>([]);

  // Load history from local storage on mount
  useEffect(() => {
    const savedHistory = localStorage.getItem('flora_guard_history');
    if (savedHistory) {
      try {
        setHistory(JSON.parse(savedHistory));
      } catch (e) {
        console.error("Failed to parse history", e);
      }
    }
  }, []);

  const saveToHistory = (item: HistoryItem) => {
    const updatedHistory = [item, ...history];
    setHistory(updatedHistory);
    localStorage.setItem('flora_guard_history', JSON.stringify(updatedHistory));
  };

  const handleImageAnalysis = async (base64: string) => {
    setIsAnalyzing(true);

    try {
      const resultRaw = await analyzePlantImage(base64);
      const result: HistoryItem = {
        ...resultRaw,
        id: crypto.randomUUID(),
        type: 'analysis',
        timestamp: Date.now(),
        imageUrl: base64,
        plantName: resultRaw.plantName || 'Unknown Plant'
      } as HistoryItem;

      saveToHistory(result);

      const botMsg: Message = {
        role: 'model',
        text: `I've completed the diagnostic scan for your plant.`,
        isAnalysis: true,
        analysis: result,
        image: base64
      };
      setMessages(prev => [...prev, botMsg]);
    } catch (err: any) {
      console.error(err);
      const errorMsg: Message = { 
        role: 'model', 
        text: "I'm sorry, I couldn't identify any issues in that photo. Could you try uploading a clearer image of the affected area or the specific bugs you see?" 
      };
      setMessages(prev => [...prev, errorMsg]);
    } finally {
      setIsAnalyzing(false);
    }
  };

  const handleSendMessage = async (text: string) => {
    const userMsg: Message = { role: 'user', text };
    setMessages(prev => [...prev, userMsg]);
    setIsChatting(true);

    try {
      const historyLog = messages.map(m => ({ role: m.role, text: m.text }));
      const responseText = await chatWithBotanist(historyLog, text);

      const botMsg: Message = { role: 'model', text: responseText || "I'm sorry, I couldn't process that request." };
      setMessages(prev => [...prev, botMsg]);
    } catch (err) {
      console.error(err);
      const botMsg: Message = { role: 'model', text: "I'm having trouble connecting right now. Please try again in a moment." };
      setMessages(prev => [...prev, botMsg]);
    } finally {
      setIsChatting(false);
    }
  };

  const renderHome = () => (
    <div className="space-y-8 animate-in fade-in duration-500 py-6 md:py-10">
      <div className="text-center space-y-3 max-w-2xl mx-auto">
        <h1 className="text-3xl md:text-4xl font-extrabold text-slate-900 leading-tight">
          Flora<span className="text-emerald-600">Guard</span> AI Plant Assistant
        </h1>
        <p className="text-slate-600 text-sm md:text-base">
          Identify pests, diseases, and bugs on your vegetables and fruits plants instantly.
        </p>
      </div>

      <div className="flex justify-center pb-20">
        <div className="w-full max-w-3xl">
          <ChatWindow 
            messages={messages} 
            onSendMessage={handleSendMessage}
            onImageUpload={handleImageAnalysis}
            isLoading={isChatting || isAnalyzing} 
          />
        </div>
      </div>

      {isAnalyzing && (
        <div className="fixed inset-0 bg-slate-900/10 backdrop-blur-sm z-50 flex flex-col items-center justify-center space-y-4">
          <div className="bg-white p-8 rounded-3xl shadow-2xl flex flex-col items-center space-y-4 border border-emerald-100">
            <div className="relative">
              <div className="w-16 h-16 border-4 border-emerald-100 border-t-emerald-600 rounded-full animate-spin"></div>
              <div className="absolute inset-0 flex items-center justify-center"><span className="text-xl">üêõ</span></div>
            </div>
            <p className="text-slate-900 font-bold animate-pulse">Running AI Diagnostics...</p>
            <p className="text-slate-500 text-xs">Scanning for diseases, pests, and bug signs</p>
          </div>
        </div>
      )}
    </div>
  );

  const renderHistory = () => (
    <div className="space-y-8 animate-in fade-in duration-500 py-10">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold text-slate-900">Unified History</h1>
          <p className="text-slate-500 mt-1">Disease analyses and garden care guides in one place.</p>
        </div>
        <button 
          onClick={() => { setHistory([]); localStorage.removeItem('flora_guard_history'); }}
          className="text-sm text-red-500 hover:text-red-700 font-medium px-4 py-2 border border-red-100 rounded-lg hover:bg-red-50"
        >
          Clear All
        </button>
      </div>

      {history.length === 0 ? (
        <div className="text-center py-24 bg-white rounded-[2.5rem] border border-dashed border-slate-200">
          <div className="text-4xl mb-4">üìú</div>
          <p className="text-slate-400 font-medium">No history found yet.</p>
          <button 
            onClick={() => setView('home')} 
            className="mt-6 px-8 py-3 bg-emerald-600 text-white rounded-2xl font-bold hover:bg-emerald-700 transition-all shadow-lg shadow-emerald-600/20"
          >
            Start Analyzing
          </button>
        </div>
      ) : (
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-8">
          {history.map((item) => (
            <div 
              key={item.id} 
              className="bg-white rounded-[2rem] overflow-hidden shadow-sm border border-slate-100 hover:shadow-xl transition-all duration-300 group cursor-pointer flex flex-col"
              onClick={() => {
                if (item.type === 'analysis') {
                  setMessages([{
                    role: 'model',
                    text: `I've reloaded the analysis for your **${item.plantName}**.`,
                    isAnalysis: true,
                    analysis: item,
                    image: item.imageUrl
                  }]);
                  setView('home');
                } else {
                  setMessages([{
                    role: 'model',
                    text: item.guideContent || ""
                  }]);
                  setView('garden-care');
                }
              }}
            >
              <div className="h-44 bg-slate-50 relative overflow-hidden">
                {item.type === 'analysis' && item.imageUrl ? (
                  <img src={`data:image/jpeg;base64,${item.imageUrl}`} className="w-full h-full object-cover transition-transform group-hover:scale-110" alt={item.diseaseName} />
                ) : (
                  <div className="w-full h-full flex flex-col items-center justify-center text-slate-300 space-y-2">
                    <span className="text-4xl">{item.type === 'guide' ? 'ü™¥' : 'üî¨'}</span>
                    <span className="text-[10px] font-black uppercase tracking-widest text-slate-400">
                      {item.type === 'guide' ? 'Garden Guide' : 'Analysis'}
                    </span>
                  </div>
                )}
                <div className="absolute top-4 left-4">
                  <span className={`px-3 py-1 rounded-full text-[10px] font-black uppercase tracking-widest shadow-lg ${item.type === 'analysis' ? 'bg-blue-600 text-white' : 'bg-emerald-600 text-white'}`}>
                    {item.type}
                  </span>
                </div>
              </div>
              <div className="p-6 flex-1 flex flex-col">
                <div className="mb-4">
                  <p className="text-[10px] font-black text-emerald-600 uppercase tracking-widest">{item.plantName}</p>
                  <h3 className="font-black text-slate-900 mt-1 line-clamp-1">
                    {item.type === 'analysis' ? item.diseaseName : `Care Guide: ${item.plantName}`}
                  </h3>
                </div>

                {item.type === 'guide' && (
                  <div className="text-xs text-slate-500 line-clamp-3 mb-4 prose prose-xs">
