--------------------------------------------------
This report was generated by FloraGuard AI Assistant.
Always consult with a local agricultural extension officer for regional-specific advice.
    `.trim();

    const blob = new Blob([reportContent], { type: 'text/plain' });
    const url = URL.createObjectURL(blob);
    const link = document.createElement('a');
    link.href = url;
    link.download = `FloraGuard_Report_${analysis.plantName.replace(/\s+/g, '_')}.txt`;
    document.body.appendChild(link);
