  "bestSoil": string,
  "growthTips": string[]
}`;

export const analyzePlantImage = async (base64Image: string): Promise<Partial<HistoryItem>> => {
  const model = 'gemini-3-flash-preview';

  const prompt = `Analyze this vegetable plant leaf image. Identify if it has a disease or a pest/bug infestation. Identify:
1. The common name of the plant.
2. The specific identification name (e.g., the disease name or the bug/pest name).
3. Visual symptoms or signs of pests (like webbing, holes, or spots) observed.
4. Organic cures or natural deterrents.
5. Recommended chemical/pesticide treatments.
6. MANDATORY: For every chemical pesticide mentioned, provide a direct search or purchase URL.
7. Preventive measures to stop the return.

Return the data strictly in JSON format matching this schema:
{
  "plantName": string,
  "diseaseName": string, 
  "severity": "Low" | "Medium" | "High",
  "symptoms": string[],
  "cures": {
    "organic": string[],
    "chemical": string[]
  },
  "purchaseLinks": [
    { "pesticideName": string, "url": string }
  ],
  "prevention": string[]
}
Note: "diseaseName" should be the name of the pest if it's a bug infestation. Ensure EVERY chemical listed in cures.chemical has a corresponding entry in purchaseLinks.`;

  const response = await ai.models.generateContent({
    model,
    contents: {
      parts: [
        { inlineData: { mimeType: 'image/jpeg', data: base64Image } },
        { text: prompt }
      ]
    },
    config: {
      systemInstruction: SYSTEM_INSTRUCTION,
      responseMimeType: "application/json"
    }
  });

  const text = response.text;
  if (!text) throw new Error("No response from AI");

  try {
    return JSON.parse(text);
  } catch (err) {
    console.error("Failed to parse AI response:", text);
    throw new Error("Invalid response format from AI");
  }
};

