interface SeedDetectorProps {
  onAddToHistory: (item: HistoryItem) => void;
}

const SeedDetector: React.FC<SeedDetectorProps> = ({ onAddToHistory }) => {
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [result, setResult] = useState<HistoryItem['seedData'] | null>(null);
  const [currentImage, setCurrentImage] = useState<string | null>(null);
  const fileInputRef = useRef<HTMLInputElement>(null);

  const handleFileUpload = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (file) {
      const reader = new FileReader();
      reader.onloadend = async () => {
        const base64String = (reader.result as string).split(',')[1];
        setCurrentImage(base64String);
        await performAnalysis(base64String);
      };
      reader.readAsDataURL(file);
    }
  };

  const performAnalysis = async (base64: string) => {
    setIsAnalyzing(true);
    setResult(null);
    try {
      const data = await analyzeSeedImage(base64);
      setResult(data);

      const historyItem: HistoryItem = {
        id: crypto.randomUUID(),
        timestamp: Date.now(),
        type: 'seed-analysis',
        plantName: data.seedName,
        imageUrl: base64,
        seedData: data
      };
      onAddToHistory(historyItem);
    } catch (err) {
      alert("Failed to identify the seed. Please try a clearer image.");
