                    analysis: item,
                    image: item.imageUrl
                  }]);
                  setView('home');
                } else if (item.type === 'soil-analysis') {
                  setSelectedHistoryItem(item);
                  setView('soil-analyzer');
                } else if (item.type === 'seed-analysis') {
                  setSelectedHistoryItem(item);
                  setView('seed-detector');
                } else {
                  setMessages([{
                    role: 'model',
                    text: item.guideContent || ""
                  }]);
                  setView('garden-care');
                }
              }}
            >
              <div className="h-44 bg-slate-50 relative overflow-hidden">
                {item.imageUrl ? (
                  <img src={`data:image/jpeg;base64,${item.imageUrl}`} className="w-full h-full object-cover transition-transform group-hover:scale-110" alt={item.plantName} />
                ) : (
                  <div className="w-full h-full flex flex-col items-center justify-center text-slate-300 space-y-2">
                    <span className="text-4xl">{item.type === 'guide' ? 'ü™¥' : item.type === 'soil-analysis' ? 'üî¨' : item.type === 'seed-analysis' ? 'üå±' : 'üî¨'}</span>
                  </div>
                )}
                <div className="absolute top-4 left-4">
                  <span className={`px-3 py-1 rounded-full text-[10px] font-black uppercase tracking-widest shadow-lg ${
                    item.type === 'analysis' ? 'bg-blue-600 text-white' : 
                    item.type === 'soil-analysis' ? 'bg-amber-600 text-white' : 
                    item.type === 'seed-analysis' ? 'bg-emerald-800 text-white' : 
                    'bg-emerald-600 text-white'}`}>
                    {item.type.replace('-', ' ')}
                  </span>
                </div>
              </div>
              <div className="p-6 flex-1 flex flex-col">
                <div className="mb-4">
                  <p className="text-[10px] font-black text-emerald-600 uppercase tracking-widest">{item.plantName}</p>
                  <h3 className="font-black text-slate-900 mt-1 line-clamp-1">
                    {item.type === 'analysis' ? item.diseaseName : 
                     item.type === 'soil-analysis' ? `Soil Health: pH ${item.soilData?.phValue}` : 
                     item.type === 'seed-analysis' ? `Seed: ${item.seedData?.seedName}` :
                     `Care Guide: ${item.plantName}`}
                  </h3>
                </div>

                <div className="mt-auto flex items-center justify-between">
                  <span className="text-[10px] font-bold text-slate-400">{new Date(item.timestamp).toLocaleDateString()}</span>
                  <div className="flex items-center text-[10px] font-black text-emerald-600 uppercase tracking-widest group-hover:translate-x-1 transition-transform">
                    <span>View Result</span>
                    <svg className="w-3 h-3 ml-1" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                      <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="3" d="M9 5l7 7-7 7" />
                    </svg>
                  </div>
                </div>
              </div>
            </div>
          ))}
        </div>
      )}
    </div>
  );

  const renderAccount = () => (
    <div className="max-w-2xl mx-auto space-y-8 animate-in fade-in duration-500 py-10">
      <div>
        <h1 className="text-3xl font-bold text-slate-900">Account Details</h1>
        <p className="text-slate-500 mt-1">Manage your profile and subscription.</p>
      </div>

      <div className="bg-white rounded-[2.5rem] border border-slate-100 shadow-sm divide-y divide-slate-50 overflow-hidden">
        <div className="p-8 flex items-center space-x-6">
          <div className="w-20 h-20 rounded-full bg-emerald-100 flex items-center justify-center text-emerald-600 text-2xl font-black">
            JD
          </div>
          <div>
            <h3 className="text-xl font-bold text-slate-900">John Doe</h3>
            <p className="text-slate-500 text-sm">john.doe@flora-guard.ai</p>
            <div className="mt-2 inline-flex items-center px-2 py-1 bg-emerald-50 text-emerald-700 text-xs font-bold rounded">
              PREMIUM AGRO PLAN
            </div>
          </div>
        </div>

        <div className="p-8 space-y-6">
          <div className="flex justify-between items-center">
            <div>
              <p className="text-sm font-bold text-slate-800">Monthly Usage</p>
              <p className="text-xs text-slate-500">22 of 100 scans used</p>
            </div>
            <div className="w-32 h-2 bg-slate-100 rounded-full overflow-hidden">
              <div className="h-full bg-emerald-500" style={{ width: '22%' }}></div>
            </div>
          </div>

          <div className="space-y-4">
            <h4 className="text-xs font-bold text-slate-400 uppercase tracking-widest">Preferences</h4>
            <div className="flex items-center justify-between">
              <span className="text-sm text-slate-700 font-medium">Cloud History Backup</span>
              <div className="w-10 h-5 bg-emerald-600 rounded-full relative cursor-pointer">
                <div className="absolute right-1 top-1 w-3 h-3 bg-white rounded-full shadow-sm"></div>
              </div>
            </div>
          </div>
        </div>

        <div className="p-8 bg-slate-50/50">
          <button className="w-full py-4 bg-white border border-slate-200 text-slate-700 rounded-2xl font-bold text-sm hover:bg-slate-50 transition-all shadow-sm">
            Sign Out
          </button>
        </div>
