export type AppView = 'home' | 'history' | 'account' | 'garden-care' | 'soil-analyzer' | 'seed-detector';

export interface User {
  name: string;
  email: string;
  photoUrl: string;
  isLoggedIn: boolean;
}

export interface Message {
  role: 'user' | 'model';
  text: string;
  image?: string;
  isAnalysis?: boolean;
  analysis?: HistoryItem;
}

export interface PurchaseLink {
  pesticideName: string;
  url: string;
}

export type HistoryItemType = 'analysis' | 'guide' | 'soil-analysis' | 'seed-analysis';

export interface HistoryItem {
  id: string;
  timestamp: number;
  type: HistoryItemType;
  plantName: string;
  // Fields for 'analysis' type
  diseaseName?: string;
  severity?: string;
  symptoms?: string[];
  cures?: {
    organic: string[];
    chemical: string[];
  };
  prevention?: string[];
  purchaseLinks?: PurchaseLink[];
  imageUrl?: string;
  // Field for 'guide' type
  guideContent?: string;
  // Fields for 'soil-analysis' type
  soilData?: {
    phValue: string;
    nitrogen: string;
    phosphorus: string;
    potassium: string;
    organicMatter: string;
    suitableCrops: string[];
    improvementTips: string[];
  };
  // Fields for 'seed-analysis' type
  seedData?: {
    seedName: string;
    plantName: string;
    description: string;
    cultivationPlaces: string[];
    bestSoil: string;
    growthTips: string[];
  };
}

export interface GardenPlant {
  id: string;
  name: string;
  description: string;
  imageUrl: string;
  pottingMix: {
    soil: number;
    sand: number;
    compost: number;
    others?: string;
  };
  watering: string;
  sunlight: string;
  floweringSeason: string;
}

export interface ChatState {
  messages: Message[];
  isAnalyzing: boolean;
  error: string | null;
}

import React, { useState, useEffect } from 'react';
import { Message, HistoryItem, AppView, User } from './types';
import { analyzePlantImage, chatWithBotanist } from './services/geminiService';
import ChatWindow from './components/ChatWindow';
import Sidebar from './components/Sidebar';
import GardenCare from './components/GardenCare';
import SoilAnalyzer from './components/SoilAnalyzer';
import SeedDetector from './components/SeedDetector';
import ReactMarkdown from 'react-markdown';
import remarkGfm from 'remark-gfm';

const App: React.FC = () => {
  const [view, setView] = useState<AppView>('home');
  const [isAnalyzing, setIsAnalyzing] = useState(false);
  const [isChatting, setIsChatting] = useState(false);
  const [messages, setMessages] = useState<Message[]>([]);
  const [history, setHistory] = useState<HistoryItem[]>([]);
  const [selectedHistoryItem, setSelectedHistoryItem] = useState<HistoryItem | null>(null);
  const [isMobileMenuOpen, setIsMobileMenuOpen] = useState(false);
  const [user, setUser] = useState<User>({
    name: '',
    email: '',
    photoUrl: '',
    isLoggedIn: false
  });

  // Load history and user session from local storage on mount
  useEffect(() => {
    const savedHistory = localStorage.getItem('flora_guard_history');
    if (savedHistory) {
      try {
        setHistory(JSON.parse(savedHistory));
      } catch (e) {
        console.error("Failed to parse history", e);
      }
    }

    const savedUser = localStorage.getItem('flora_guard_user');
    if (savedUser) {
      try {
        setUser(JSON.parse(savedUser));
      } catch (e) {
        console.error("Failed to parse user session", e);
      }
    }
  }, []);

  const saveToHistory = (item: HistoryItem) => {
    const updatedHistory = [item, ...history];
    setHistory(updatedHistory);
    localStorage.setItem('flora_guard_history', JSON.stringify(updatedHistory));
  };

  const handleViewChange = (newView: AppView) => {
    setView(newView);
    setSelectedHistoryItem(null); 
    setIsMobileMenuOpen(false); 
  };

  const handleGoogleLogin = () => {
    // Simulating a Google OAuth response
    const mockUser: User = {
      name: 'Agro Enthusiast',
      email: 'agro.enthusiast@gmail.com',
      photoUrl: 'https://api.dicebear.com/7.x/avataaars/svg?seed=Agro',
      isLoggedIn: true
    };
    setUser(mockUser);
    localStorage.setItem('flora_guard_user', JSON.stringify(mockUser));
  };

  const handleSignOut = () => {
    const loggedOutUser = { name: '', email: '', photoUrl: '', isLoggedIn: false };
    setUser(loggedOutUser);
    localStorage.removeItem('flora_guard_user');
    setView('home');
  };

  const handleImageAnalysis = async (base64: string) => {
    setIsAnalyzing(true);

    try {
      const resultRaw = await analyzePlantImage(base64);
      const result: HistoryItem = {
        ...resultRaw,
        id: crypto.randomUUID(),
        type: 'analysis',
        timestamp: Date.now(),
        imageUrl: base64,
        plantName: resultRaw.plantName || 'Unknown Plant'
      } as HistoryItem;

      saveToHistory(result);

      const botMsg: Message = {
        role: 'model',
        text: `I've completed the diagnostic scan for your plant.`,
        isAnalysis: true,
        analysis: result,
        image: base64
      };
      setMessages(prev => [...prev, botMsg]);
    } catch (err: any) {
      console.error(err);
      const errorMsg: Message = { 
        role: 'model', 
        text: "I'm sorry, I couldn't identify any issues in that photo. Could you try uploading a clearer image of the affected area or the specific bugs you see?" 
      };
      setMessages(prev => [...prev, errorMsg]);
    } finally {
      setIsAnalyzing(false);
    }
  };

  const handleSendMessage = async (text: string) => {
    const userMsg: Message = { role: 'user', text };
    setMessages(prev => [...prev, userMsg]);
    setIsChatting(true);

    try {
      const historyLog = messages.map(m => ({ role: m.role, text: m.text }));
      const responseText = await chatWithBotanist(historyLog, text);

      const botMsg: Message = { role: 'model', text: responseText || "I'm sorry, I couldn't process that request." };
      setMessages(prev => [...prev, botMsg]);
    } catch (err) {
      console.error(err);
      const botMsg: Message = { role: 'model', text: "I'm having trouble connecting right now. Please try again in a moment." };
      setMessages(prev => [...prev, botMsg]);
    } finally {
      setIsChatting(false);
    }
  };

  const renderHome = () => (
    <div className="space-y-8 animate-in fade-in duration-500 py-6 md:py-10">
      <div className="text-center space-y-3 max-w-2xl mx-auto">
        <h1 className="text-3xl md:text-4xl font-extrabold text-slate-900 leading-tight">
          Flora<span className="text-emerald-600">Guard</span> AI Plant Assistant
