    e.preventDefault();
    if (!city.trim()) return;

    setLoading(true);
    setWeatherData(null);
    setError(null);

    try {
      const data = await getWeatherDetails(city);
      setWeatherData(data);
    } catch (err) {
      setError("Could not fetch weather data. Please try again.");
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="space-y-12 animate-in fade-in duration-500 py-10 pb-20">
      <div className="max-w-4xl mx-auto space-y-6 text-center">
        <h1 className="text-4xl font-black text-slate-900 tracking-tight">Farm Weather & Risks</h1>
        <p className="text-slate-500 max-w-2xl mx-auto">
          Real-time weather updates and disaster alerts (floods, cyclones) for your farming location.
        </p>
      </div>

      <div className="max-w-3xl mx-auto">
        <form onSubmit={handleSearch} className="relative group z-20">
          <input
            type="text"
            value={city}
            onChange={(e) => setCity(e.target.value)}
            placeholder="Enter your City or Region (e.g. Kochi, Kerala)"
            className="w-full bg-white border-2 border-slate-100 rounded-3xl px-8 py-6 text-lg text-slate-900 placeholder-slate-400 outline-none focus:border-emerald-500 shadow-xl shadow-slate-200/50 transition-all"
            disabled={loading}
          />
          <button
            type="submit"
            disabled={loading || !city.trim()}
            className="absolute right-3 top-3 bottom-3 bg-emerald-600 hover:bg-emerald-700 text-white px-8 rounded-2xl font-bold transition-all active:scale-95 disabled:opacity-50 flex items-center"
          >
            {loading ? (
               <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin"></div>
            ) : (
               <span>Check</span>
            )}
          </button>
        </form>

        {error && (
           <div className="mt-8 p-6 bg-red-50 text-red-600 rounded-2xl text-center font-medium border border-red-100">
             {error}
           </div>
        )}

        {weatherData && (
          <div className="mt-10 animate-in slide-in-from-bottom-8">
            <div className="bg-gradient-to-br from-blue-500 to-blue-700 rounded-[2.5rem] p-8 md:p-12 text-white shadow-2xl relative overflow-hidden">
               {/* Decorative background elements */}
               <div className="absolute top-0 right-0 -mt-10 -mr-10 w-64 h-64 bg-white/10 rounded-full blur-3xl"></div>
               <div className="absolute bottom-0 left-0 -mb-10 -ml-10 w-64 h-64 bg-black/10 rounded-full blur-3xl"></div>

               <div className="relative z-10">
                 <div className="flex items-center space-x-3 mb-6 opacity-80">
                   <svg className="w-5 h-5" fill="none" stroke="currentColor" viewBox="0 0 24 24"><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M17.657 16.657L13.414 20.9a1.998 1.998 0 01-2.827 0l-4.244-4.243a8 8 0 1111.314 0z" /><path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M15 11a3 3 0 11-6 0 3 3 0 016 0z" /></svg>
                   <span className="text-sm font-bold uppercase tracking-widest">{city}</span>
                 </div>

                 <div className="prose prose-invert max-w-none prose-headings:font-black prose-headings:text-white prose-p:text-blue-50 prose-li:text-blue-50 prose-strong:text-white">
                    <ReactMarkdown remarkPlugins={[remarkGfm]}>
                      {weatherData.text}
                    </ReactMarkdown>
                 </div>

                 {weatherData.groundingMetadata?.groundingChunks && (
                    <div className="mt-8 pt-6 border-t border-white/10">
                      <p className="text-[10px] font-black uppercase tracking-widest opacity-60 mb-3">Sources</p>
                      <div className="flex flex-wrap gap-2">
                        {weatherData.groundingMetadata.groundingChunks.map((chunk: any, i: number) => (
                           chunk.web?.uri && (
                             <a 
                               key={i} 
                               href={chunk.web.uri} 
                               target="_blank" 
                               rel="noreferrer"
                               className="px-3 py-1.5 bg-white/10 hover:bg-white/20 rounded-lg text-xs truncate max-w-[200px] transition-colors"
                             >
                               {chunk.web.title || new URL(chunk.web.uri).hostname}
                             </a>
                           )
                        ))}
                      </div>
                    </div>
                 )}
               </div>
            </div>

            <div className="mt-8 grid grid-cols-1 md:grid-cols-2 gap-6">
               <div className="bg-white p-8 rounded-3xl border border-slate-100 shadow-lg">
                  <div className="w-12 h-12 bg-red-50 text-red-500 rounded-2xl flex items-center justify-center text-2xl mb-4">üå™Ô∏è</div>
                  <h3 className="text-lg font-black text-slate-900 mb-2">Disaster Risk Check</h3>
                  <p className="text-slate-500 text-sm">
                    Always verify flood and cyclone warnings with local government authorities. AI predictions are based on available online data.
                  </p>
               </div>
               <div className="bg-white p-8 rounded-3xl border border-slate-100 shadow-lg">
                  <div className="w-12 h-12 bg-emerald-50 text-emerald-500 rounded-2xl flex items-center justify-center text-2xl mb-4">üöú</div>
                  <h3 className="text-lg font-black text-slate-900 mb-2">Farming Advisory</h3>
                  <p className="text-slate-500 text-sm">
                    Use the weather outlook to plan irrigation and pesticide application. Avoid spraying before rains.
                  </p>
               </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

